import React, { useState, useRef } from 'react';
import { API_URL, WS_URL } from './config';

type Message = {
  type: 'interviewer_response' | 'interview_complete';
  message?: string;
  assessment?: any;
  timestamp: string;
};

function App() {
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const wsRef = useRef<WebSocket | null>(null);

  const startInterview = async () => {
    const res = await fetch(`${API_URL}/api/interview/start`, { method: 'POST' });
    const data = await res.json();
    setSessionId(data.session_id);

    const ws = new WebSocket(`${WS_URL}/${data.session_id}`);
    wsRef.current = ws;

    ws.onopen = () => console.log('WebSocket connected');
    ws.onmessage = event => {
      const msg: Message = JSON.parse(event.data);
      setMessages(prev => [...prev, msg]);
    };
    ws.onerror = err => console.error('WebSocket error', err);
    ws.onclose = () => console.log('WebSocket closed');
  };

  const sendCandidateResponse = (text: string) => {
    if (!wsRef.current) return;
    wsRef.current.send(JSON.stringify({ type: 'candidate_response', message: text }));
  };

  const endInterview = () => {
    if (!wsRef.current) return;
    wsRef.current.send(JSON.stringify({ type: 'end_interview' }));
  };

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h1>AI Excel Interviewer</h1>
      {!sessionId ? (
        <button onClick={startInterview}>Start Interview</button>
      ) : (
        <>
          <div style={{
            border: '1px solid #ccc',
            padding: 10,
            height: 300,
            overflowY: 'auto',
            marginBottom: 10
          }}>
            {messages.map((m, i) => (
              <div key={i} style={{ marginBottom: 8 }}>
                {m.type === 'interviewer_response' && (
                  <p><strong>Interviewer:</strong> {m.message}</p>
                )}
                {m.type === 'interview_complete' && (
                  <pre>{JSON.stringify(m.assessment, null, 2)}</pre>
                )}
              </div>
            ))}
          </div>
          <input
            type="text"
            placeholder="Your answer..."
            style={{ width: '80%', marginRight: 8 }}
            onKeyDown={e => {
              if (e.key === 'Enter') {
                sendCandidateResponse((e.target as HTMLInputElement).value);
                (e.target as HTMLInputElement).value = '';
              }
            }}
          />
          <button onClick={endInterview}>End Interview</button>
        </>
      )}
    </div>
  );
}

export default App;
